import Raect,{useState,useEffect} from "react";
import "./index.css";
import EditIcon from "@material-ui/icons/Edit";

const getLocalItem = ()=>{
  let local = localStorage.getItem("Lists");
  if(local){
    return(JSON.parse(local));

  }
  else
    return([]);
}

const Todo = ()=>{

const[set,setName] = useState("");

const[list,setList] = useState(getLocalItem());

const[toggle,setToggle] = useState(true);

const[valu,edtValu] = useState(null);

const handle = (e)=>{
	setName(e.target.value);
};

const listName = ()=>{
  if(!set)
  {
  alert("Please fill data");
  }
  else if(set && !toggle){
    setList(
      list.map((val)=>{

     if(val.id==valu)
     {
      return{...val,name:set}
     }

    })
      );
  }
  else{
const allSet = {
  id:new Date().getTime().toString(),
  name:set
};
	setList([...list, allSet]);
  setName("");
}
};

const deleteItem = (idx)=>{
const updated = list.filter((val)=>{
  return idx!==val.id;
})
setList(updated);
};

const removeItm = ()=>{
  setList([]);
};
if(list.length===0){
  var x = true;
}

useEffect(()=>{

localStorage.setItem("Lists",JSON.stringify(list))

},[list] );

const editVal = (edtId)=>{

  const newEditItem = list.find((val)=>{

    return(val.id==edtId);

  });
  
  setToggle(false);
  setName(newEditItem.name);
  edtValu(edtId);

}

	return(<>

    <div className="header">
                 <h2>My To Do List</h2>
       <input type="text" onChange={handle} value={set} placeholder="Title..." />
      {
        toggle? <span onClick={listName} className="addBtn">Add</span>:
        <span onClick={listName} className="addBt">{<EditIcon/>}</span>
      }
     
    </div>

<ul>
{x?
   <th style={{fontSize:25}}> <span style={{color:"red"}}>Data is Not Available</span></th>:
  <th style={{fontSize:25}}>Added List Data:</th>
  
}
  {
    list.map((val)=>{
      return(
<div>
   <span onClick={ ()=>editVal(val.id)} className="edt">{<EditIcon/>}</span>

      <li className="lst" key={val.id}>{val.name}

       <span onClick={ ()=>deleteItem(val.id)} className="rmv">Delete</span>

      </li>
      
  </div>
      )
    })
  }

  <br/><br/>
<span onClick={removeItm} className="rmv1">Remove All</span>

</ul>

		</>)
};

export default Todo;
