import Raect,{useState} from "react";
import "./index.css";

const Todo = ()=>{

const[set,setName] = useState("");

const[list,setList] = useState([]);

const handle = (e)=>{
	setName(e.target.value);
};

const listName = ()=>{
  if(!set)
  {

  }
  else{
	setList([...list,set]);
  setName("");
}
};

const deleteItem = (id)=>{
const updated = list.filter((val,ind)=>{
  return ind!==id;
})
setList(updated);
};

const removeItm = ()=>{
  setList([]);
};

	return(<>

    <div className="header">
                 <h2>My To Do List</h2>
       <input type="text" onChange={handle} value={set} placeholder="Title..." />
      
      <span onClick={listName} className="addBtn">Add</span>
    </div>

<ul>
  <li>Hit the gym</li>
  {
    list.map((val,ind)=>{
      return(

      <li className="lst" key={ind}>{val}

       <span onClick={ ()=>deleteItem(ind)} className="rmv">Delete</span>

      </li>
  
      )
    })
  }

  <br/><br/>
<span onClick={removeItm} className="rmv1">Remove</span>

</ul>

		</>)
};

export default Todo;
