import React from "react";
import ReactDOM from "react-dom";

//import "./index.css";

const fname = "shubham mishra";

//{}-you can only use expression and variable but not use in statement

const img1 = "https://picsum.photos/200/300";
const img2 = "https://picsum.photos/250/300";
const img3 = "https://picsum.photos/300/300";
const link = "https://www.bing.com/search?q=youtube&cvid=77a4954a67004728b049817b53f59298&DAF0=1&PC=U531";

const heading  = {
	color: "#fa9191",
	textAlign: "center",
	textTransform: "capitalize",
	fontWeight: "bold"
}

ReactDOM.render(<>
       <h1 style={ {
	color: '#fa9191',
	textAlign: 'center',
	textTransform: 'capitalize',
	fontWeight: 'bold'
} }>my name is {fname}</h1>
	<img src={img1} alt="randomImages"/>
	<img src={img2} alt="randomImages"/>
	<a href={link} target="_blank">
	<img src={img3} alt="randomImages"/>
	</a>
	</>,
	document.getElementById("root")
	);
