import React,{useState} from "react";



const App = ()=>{


const[name,Rname] = useState("");

const[sirname,Rsirname]=useState("");

const[fname,Sname] = useState("");

const inputEvent = (event)=>{

Rname(event.target.value);
	
};

const inputEvent2 = (event)=>{
	Rsirname(event.target.value);
};

const Fname = (event)=>{
event.preventDefault();
if(name==""||sirname=="")
alert('please enter your name');
else
	Sname(`${name}  ${sirname}`);
};

return(<>
<form onSubmit={Fname}>
	<div>

         <h1>Hello {fname}</h1>
         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}
         value={name} autocomplete="off"/>

         <input type="text" placeholder="Enter Your Last Name" onChange={inputEvent2}
         value={sirname} autocomplete="off"/>

         <button type="submit">Click Me</button>
        
	</div>
	</form>
	</>
	);
};

export default App;
