import React,{useState} from "react";



const App = ()=>{


const[name,Rname] = useState('shubham');

const inputEvent = (event)=>{

Rname(event.target.value);
	
};

return(<>
	<div>

         <h1>Hello {name}</h1>
         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}
         value={name}/>
         <button >Click Me</button>
        
	</div>
	</>
	);
};

export default App;
