import React,{useState} from "react";
import SubscriptionsIcon from '@material-ui/icons/Subscriptions';
import Button from '@material-ui/core/Button';

const App = ()=>{

const[fullName,setFullName] = useState({
	fname:"",
	lname:"",
	email:"",
	phone:""
});

const inputEvent= (event)=>{

	const{value,name}= event.target;

	setFullName((preVal)=>{
	return{
	...preVal,
	[name]:value,
	};


	});
};

const Fname = (event)=>{
event.preventDefault();
alert("form submitted");
};

return(<>
<form onSubmit={Fname}>
	<div>

         <h1>Hello {fullName.fname} {fullName.lname}</h1>

           <p>{fullName.email} </p>
           <p>{fullName.phone}</p> 

         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}
         name="fname" value={fullName.fname}/>

         <input type="text" placeholder="Enter Your Last Name" onChange={inputEvent} name="lname" value={fullName.lname}/>

         <input type="email" placeholder="Enter Your Email" onChange={inputEvent} name="email" value={fullName.email}/>

         <input type="number" placeholder="Enter Your phone no." onChange={inputEvent} name="phone" value={fullName.phone}/>

         <Button type="submit"><SubscriptionsIcon/></Button>
        
	</div>
	</form>
	</>
	);
};

export default App;
