import React,{useState} from "react";



const App = ()=>{


const[name,Rname] = useState('shubham');

const[fname,Sname] = useState();

const inputEvent = (event)=>{

Rname(event.target.value);
	
};

const Fname = ()=>{
	Sname(Rname);
};

return(<>
	<div>

         <h1>Hello {fname}</h1>
         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}
         value={name}/>
         <button onClick={Fname}>Click Me</button>
        
	</div>
	</>
	);
};

export default App;
