import Raect,{useState} from "react";
import "./index.css";

const Todo = ()=>{

const[set,setName] = useState("");

const[list,setList] = useState([]);

const handle = (e)=>{
	setName(e.target.value);
};

const listName = ()=>{
  
	setList([...list,set]);
  setName("");
  
};

	return(<>

    <div className="header">
                 <h2>My To Do List</h2>
       <input type="text" onChange={handle} placeholder="Title..." />
      <button className="addBtn" onClick={listName}>Add</button>
    </div>

<ul >
  <li>Hit the gym</li>
  {
    list.map((val)=>{
      return(
      <li>{list}</li>
      )
    })
  }
  
</ul>

		</>)
};

export default Todo;
