import React,{ useState ,useEffect } from "react";

const App = ()=>{
const[num,setNum] = useState(0);
const[nums,setNums] = useState(0);
useEffect(()=>{
	document.title = `you clicked me ${num} times`;
},[num]);

const sub = ()=>{
	setNum(num+1);
};
const sub1 = ()=>{
	setNums(nums+1);
};
	return(
	<>
	<button onClick={sub}>clicked {num}</button>
	<br/>
	<button onClick={sub1}>clicked {nums}</button>
	</>
	);
};

export default App;
