import Raect,{useState} from "react";
import "./index.css";

const Todo = ()=>{

const[set,setName] = useState("my name is shubham mishra");

const handle = (e)=>{
	setName(e.target.value);
};

	return(<>

    <div className="header">
                 <h2>My To Do List</h2>
       <input type="text" onChange={handle} placeholder="Title..." />
      <button className="addBtn">Add</button>
    </div>

<ul id="myUL">
  <li>Hit the gym</li>
  <li>{set}</li>
</ul>

		</>)
};

export default Todo;
