import Raect,{useState,useEffect} from "react";
import "./index.css";

const Todo = ()=>{

const[set,setName] = useState("");

const[list,setList] = useState([]);

const handle = (e)=>{
	setName(e.target.value);
};

const listName = ()=>{
  if(!set)
  {

  }
  else{
	setList([...list,set]);
  setName("");
}
};

const deleteItem = (id)=>{
const updated = list.filter((val,ind)=>{
  return ind!==id;
})
setList(updated);
};

const removeItm = ()=>{
  setList([]);
};
if(list.length===0){
  var x = true;
}

useEffect(()=>{

localStorage.setItem("Lists",JSON.stringify(list))

},[list] );

	return(<>

    <div className="header">
                 <h2>My To Do List</h2>
       <input type="text" onChange={handle} value={set} placeholder="Title..." />
      
      <span onClick={listName} className="addBtn">Add</span>
    </div>

<ul>
{x?
   <th style={{fontSize:25}}> <span style={{color:"red"}}>Data is Not Available</span></th>:
  <th style={{fontSize:25}}>Added List Data:</th>
  
}
  {
    list.map((val,ind)=>{
      return(
<div>

      <li className="lst" key={ind}>{val}

       <span onClick={ ()=>deleteItem(ind)} className="rmv">Delete</span>

      </li>
      
  </div>
      )
    })
  }

  <br/><br/>
<span onClick={removeItm} className="rmv1">Remove All</span>

</ul>

		</>)
};

export default Todo;
