import React,{useEffect,useState} from "react";
import "./style.css";

const Chal = ()=>{

  const[city,setCity] = useState(null);
  const[search,setSearch] = useState("Mumbai");

  useEffect(()=>{
  const fetchApi = async ()=>{
  	const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=f96235e70b6711a73ae95e4f89371b9a`;
     const response = await fetch(url);
     const resJson = await response.json();
     //console.log(resJson);
     setCity(resJson);
  }

  	fetchApi();
  },[setSearch]);

	return(<>
       <div className="box">
            <div className="inputData">
                 <input type="search" className="inputField" 
                  onChange = {(event)=>{
                       setSearch(event.target.value)   
                  }} />
              
            </div>

        <div className="info">
          <h2 className="location">
          <i className="fas fa-street-view"></i>
          {search} 
          </h2>
          <h1 className="temp"> 5.25°C</h1>
          <h3 className="tempmin_max"> Min : 5.25°C | Max : 5.25°C </h3>
       </div>
       <div className="wave -one"></div>
       <div className="wave -two"></div>
       <div className="wave -three"></div>

       </div>

       </>
	)
};

export default Chal;
