import React,{useState} from "react";



const App = ()=>{
const a = "Hello";

const[name,Rname] = useState(a);

const inputEvent = (event)=>{

Rname(event);
	
};

return(<>
	<div>

         <h1>{name}</h1>
         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}/>
         <button >Click Me</button>
        
	</div>
	</>
	);
};

export default App;
