import React,{useState} from "react";



const App = ()=>{


const[fullName,setFullName] = useState({
	fname:"",
	lname:"",
	email:"",
	phone:""
});

const inputEvent= (event)=>{

	const{value,name}= event.target;

	setFullName((preVal)=>{
	if(name==="fname"){
	return{
	fname:value,
	lname:preVal.lname,
	email:preVal.email,
	phone:preVal.phone
	};
	}
	else if(name==="lname"){
	return{
	fname:preVal.fname,
	lname:value,
	email:preVal.email,
	phone:preVal.phone
	};
	}
	else if(name==="email"){
	return{
	fname:preVal.fname,
	lname:preVal.lname,
	email:value,
	phone:preVal.phone
	};
	}
	else if(name==="phone"){
	return{
	fname:preVal.fname,
	lname:preVal.lname,
	email:preVal.email,
	phone:value
	};
	}

	});
};

const Fname = (event)=>{
event.preventDefault();
alert("form submitted");
};

return(<>
<form onSubmit={Fname}>
	<div>

         <h1>Hello {fullName.fname} {fullName.lname}</h1>
<div>
           <p>{fullName.email} </p>
           <p>{fullName.phone}</p> 
           </div>

         <input type="text" placeholder="Enter Your Name" onChange={inputEvent}
         name="fname" value={fullName.fname}/>

         <input type="text" placeholder="Enter Your Last Name" onChange={inputEvent} name="lname" value={fullName.lname}/>

         <input type="email" placeholder="Enter Your Email" onChange={inputEvent} name="email" value={fullName.email}/>

         <input type="number" placeholder="Enter Your phone no." onChange={inputEvent} name="phone" value={fullName.phone}/>

         <button type="submit">Click Me</button>
        
	</div>
	</form>
	</>
	);
};

export default App;
